    },
    {
        title: "Public Relations & Media",
        image: "/images/pr.webp",
        description: "Shape public perception with strategic PR campaigns. We craft compelling narratives that build trust and enhance brand reputation.",
    },
];

export const Services = () => {
    const [activeService, setActiveService] = useState(0);
    const [isInteracted, setIsInteracted] = useState(false);
    const [isInView, setIsInView] = useState(false);

    const handleClick = (index) => {
        setActiveService(index);
        setIsInteracted(true);
    };

    const handlePrevious = () => {
        setActiveService((prev) => (prev === 0 ? servicesData.length - 1 : prev - 1));
        setIsInteracted(true);
    };

    const handleNext = () => {
        setActiveService((prev) => (prev + 1) % servicesData.length);
        setIsInteracted(true);
    };

    useEffect(() => {
        if (isInteracted || !isInView) return;

        const interval = setInterval(() => {
            setActiveService((prev) => (prev + 1) % servicesData.length);
        }, 3000);

        return () => clearInterval(interval);
    }, [isInteracted, isInView]);

    useEffect(() => {
        const handleScroll = () => {
            const servicesElement = document.querySelector('.services-wrapper');
            if (!servicesElement) return;
            const rect = servicesElement.getBoundingClientRect();
            setIsInView(rect.top >= 0 && rect.bottom <= window.innerHeight);
        };

        window.addEventListener('scroll', handleScroll);
        handleScroll(); // Initial check

        return () => window.removeEventListener('scroll', handleScroll);
    }, []);

    return (
        <section className="services-wrapper full-width">
            <div className="services-header">
            <h2 className="services-heading">Expertise You Can Rely On</h2>
            {/* <p className="services-subtext">
                Explore our range of services designed to accelerate your business growth.
            </p> */}
            </div>
            <div className='services-box'>
            <div className="services-navigation">
                <button onClick={handlePrevious} className='callback-button' aria-label='Previous Service'>
                    <FontAwesomeIcon icon={faArrowLeft} />
                </button>
            </div>

            <div className="services">
                {servicesData.map((service, index) => (
                    <article
                        key={index}
                        className={`service ${activeService === index ? "active" : ""}`}
                        onClick={() => handleClick(index)}
                        aria-labelledby={`service-title-${index}`}
                    >
                        <div className="service-image">
                            <img 
                                src={service.image} 
                                alt={`${service.title} - Creo Elements`} 
                                loading="lazy" 
                            />
                        </div>
                        <h3 id={`service-title-${index}`} className="service-title">
                            {service.title}
                        </h3>
                        <p
                            className="service-description"
                            style={{ fontSize: activeService === index ? "1rem" : "0rem" }}
                        >
                            {service.description}
                        </p>
                    </article>
                ))}
            </div>

            <button onClick={handleNext} className='callback-button' aria-label='Next Service'>
                <FontAwesomeIcon icon={faArrowRight} />
            </button>
            </div>
        </section>
    );
};

