    transform: translateY(40vh);
    position: relative;
    width: 100%;
    height: 40vh;
    margin-bottom: -10px;
    min-height: 100px;
    max-height: 40vh;
}

.parallax>.wave1 {
    animation: move-forever 25s cubic-bezier(.55, .5, .45, .5) infinite;
