    animation: move-forever 15s cubic-bezier(.55, .5, .45, .5) infinite;
}

.parallax>.wave3 {
    animation: move-forever 5s cubic-bezier(.55, .5, .45, .5) infinite;
}

.parallax>.wave4 {
